const addressSchema = new Schema ({
    street: String,
    apartment : String,
    city : String,
    state : String,
    country : String,
    zip : Number,
    isCurrentAddress : {
        type: Boolean,
        default: true
    }
});

const identityDocsSchema = new Schema ({
    number : String,
    dob : Date,
    name : String,
    fatherName : String,
    type : String,
    address : String,
    dateOfIssue : Date,
    dateOfExpire : Date,
    issuePlace: String,
    issueDate : Date,
    
});

const contactSchema = new Schema ({
    phone : Number,
    email : String,
    skype : String,
    type : String,
    isWorkAddress : {
        type: Boolean,
        default: true
    }
});

const educationSchema = new Schema ({
    degree : String,
    specialization : String,
    college : String,
    from : Date,
    to : Date,
    doc : String,
});

const experienceSchema = new Schema ({
    company : String,
    title : String,
    location : String,
    from : String,
    to : String,
    description : String,
    doc : String,
});

const USER = new Schema ({
    profilePic : String,
    firstName : String,
    lastName : String,
    title : String,
    jobLocation : String,
    department : String,
    employeeId : String,
    isAdmin : {
                type: Boolean,
                default: false
            },
    managerId : String,
    gender : String,
    dob : Date,
    dateOfJoining : Date,
    attendanceNumber : Number,
    employmentStatus : String,
    probationDate : Date,
    band : String,
    grade : String,
    professionalSummary : String,
    timeType : String,
    workerType : String,
    shift : String,
    offPolicy : String,
    holidayScheme : String,
    attendanceScheme : String,
    expensePolicy : String,
    attendanceTrackingPolicy : String,
    contact : contactSchema,
    address : addressSchema,
    education : [educationSchema],
    experience: [experienceSchema],
    identityDocs : [identityDocsSchema],
});

const attendanceSchema = new Schema ({
    employee_id : String,
    date : Date,
    time : Date,
    type : String,
})